ValidationRule.errorConditionFormula:
$Profile.Name = 'Usuario Atencion Clientes' && 
(
  ( !ISBLANK( PersonAssistantPhone ) &&  
    (
     LEN(PersonAssistantPhone ) <> 10 || 
     !ISNUMBER(PersonAssistantPhone) 
    ) 
  ) ||
  ( !ISBLANK( PersonHomePhone ) &&  
    (
     LEN(PersonHomePhone ) <> 10 || 
     !ISNUMBER(PersonHomePhone) 
    ) 
  ) ||
  ( !ISBLANK( PersonOtherPhone ) &&  
    (
     LEN(PersonOtherPhone ) <> 10 || 
     !ISNUMBER(PersonOtherPhone) 
    ) 
  ) ||
  ( !ISBLANK( Phone ) &&  
    (
     LEN(Phone ) <> 10 || 
     !ISNUMBER(Phone) 
    ) 
  ) ||
  ( !ISBLANK( PersonMobilePhone ) &&  
    (
     LEN(PersonMobilePhone ) <> 10 || 
     !ISNUMBER(PersonMobilePhone) 
    ) 
  )
)
